mapper.select:
select

from tbl_erp_hardware_product
where admin_id = #{adminId,jdbcType=CHAR}

	and create_date_time >= #{startDate,jdbcType=VARCHAR}


	and create_date_time <= #{endDate,jdbcType=VARCHAR}

order by id desc

select

from tbl_erp_hardware_product
where status=#{status}

	and category_model_format_id=#{formatId}

order by id desc
